mapper.select:

SELECT id, questionId, content, writerId, createdAt, updatedAt
FROM Answer
WHERE questionId = #{questionId}

mapper.update:

UPDATE INTO Answer (questionId, content, writerId, createdAt, updatedAt)
VALUES (#{questionId}, #{content}, #{writerId}, #{createdAt}, #{updatedAt})
WHERE answerId = #{answerId};


UPDATE INTO Question (title, content, writerId, createdAt, updatedAt)
VALUES (#{title}, #{content}, #{writer}, #{createdAt}, #{updatedAt})
WHERE questionId = #{questionId};
